import{playSound}from"./audio.js";function initWindEffect(){const t=document.getElementById("wind-container");if(t)for(let e=0;e<60;e++){const e=document.createElement("div");e.className="particle",e.style.left=Math.random()*window.innerWidth+"px",e.style.top=Math.random()*window.innerHeight+"px",e.style.animationDuration=5+15*Math.random()+"s",e.style.animationDelay=5*Math.random()+"s",t.appendChild(e)}}const createBubble=()=>{const t=document.createElement("div");t.className="bubble",t.style.left=Math.random()*window.innerWidth+"px",t.style.animationDuration=3+4*Math.random()+"s",t.style.animationDelay=2*Math.random()+"s",document.body.appendChild(t),setTimeout(()=>{t.remove()},7e3)};function createBubbles(){setInterval(createBubble,2e3)}function createShootingStar(){const t=document.createElement("div");t.className="shooting-star",t.style.left=Math.random()*window.innerWidth+"px",t.style.top=Math.random()*(window.innerHeight/2)+"px",t.style.animationDuration=1+2*Math.random()+"s",document.body.appendChild(t),setTimeout(()=>{t.remove()},3e3)}function scheduleShootingStar(){setInterval(createShootingStar,5e3+1e4*Math.random())}let rainbowInterval;function toggleRainbowMode(){document.body.classList.contains("rainbow-mode")?stopRainbowMode():startRainbowMode()}function startRainbowMode(){const t=document.body;t.classList.add("rainbow-mode");let e=0;rainbowInterval=setInterval(()=>{e=(e+1)%360,t.style.setProperty("--rainbow-hue",e)},50),playSound(440,.1)}function stopRainbowMode(){const t=document.body;t.classList.remove("rainbow-mode"),clearInterval(rainbowInterval),t.style.removeProperty("--rainbow-hue"),playSound(330,.1)}export{initWindEffect,createBubbles,scheduleShootingStar,toggleRainbowMode,startRainbowMode,stopRainbowMode};